mapper.insert:
insert into department (Id, departmentName, ministerId,
description, createTime
)
values (#{id,jdbcType=VARCHAR},
#{departmentName,jdbcType=VARCHAR},
#{ministerId,jdbcType=VARCHAR},
#{description,jdbcType=VARCHAR}, #{createTime,jdbcType=VARCHAR}
)

mapper.update:
update department


		departmentName=#{newDepartmentName,jdbcType=VARCHAR},


		ministerId=#{newMinisterId,jdbcType=VARCHAR},


		description=#{newDescription,jdbcType=VARCHAR}


where Id=#{departmentId,jdbcType=VARCHAR}
and delFlag=0
